styles.css:




.has-link-color a {
  color:#006400 !important;
}
.has-text-color h3{
  color:black !important;
}

.has-text-color.has-primary-color {
  color:#006400 !important;
}

@media (min-width: 1024px) {

  .hide-desktop{
    display:none;
  }
}


/*   for tablet (typically 768px to 1023px) */
@media (min-width: 768px) and (max-width: 1023px) {
  .custom-logo-link {
    display: none !important;
  }
  .contact7-form{
    padding-left:5%;
    padding-right:5%;
  }
}


/* for mobile (typically up to 767px) */
@media (max-width: 767px) {
  .custom-logo-link {
    display: none 
!important; /* Use !important to ensure it overrides other styles */
  }

.contact7-form{
    padding-left:5%;
    padding-right:5%;
  }
}
